m = &#123;&#125;".format(self.a, self.b, self.m)) print("state1 = &#123;&#125;".format(self.next())) print("state2 = &#123;&#125;".format(self.next()))class DH: def __init__(self): self.lcg = LCG() self.lcg.output() self.g = 183096451267674849541594370111199688704 self.m = 102752586316294557951738800745394456033378966059875498971396396583576430992701 self.A, self.a = self.gen_AB() # a private key self.B, self.b = self.gen_AB() # b private key self.key = pow(self.A, self.b, self.m) def gen_AB(self): x = '' for _ in range(64): x += '1' if self.lcg.next() % 2 else '0' return pow(self.g, int(x, 2), self.m), int(x, 2)'''def next(x): return (x * a + b) % mdef brute_force(): for i in range(1&lt;&lt;32): if i % 10000000 == 0: print("count:", i) res = next(i) if (res &gt;&gt; 16) == 16269 and (next(res) &gt;&gt; 16) == 4249: print(i) break'''DH = DH()cipher = 13040004482819935755130996285494678592830702618071750116744173145400949521388647864913527703print("g = &#123;&#125;
A = &#123;&#125;
